  def part1 do
    input()
    |> Enum.reduce([{0,0}], fn x, acc -> acc ++ [move(x, List.last(acc))] end)
    |> Enum.reduce(MapSet.new([]), fn x, acc -> MapSet.put(acc, x) end)
  end

  # Both Santa and Robo Santa take turns in moving
  def part2 do
    input()
    |> Enum.chunk_every(2)
    |> Enum.reduce([{0,0}, {0,0}], fn [santa, robo], acc -> acc ++ [move(santa, Enum.at(acc, -2)), move(robo, List.last(acc))] end)
    |> Enum.reduce(MapSet.new([]), fn x, acc -> MapSet.put(acc, x) end)
  end
  
  def move(sign, {x,y}) do
    case sign do
      "^" -> {x, y+1}
      "v" -> {x, y-1}
      ">" -> {x+1, y}
      "<" -> {x-1, y}
      _ -> {x,y}
    end
  end
end

IO.puts "Houses visited: #{Enum.count(Day03.part1())}"
IO.puts "Houses visited by both Santa and Robo: #{Enum.count(Day03.part2())}"
